import com.example.student.model.User;
import com.example.student.request.LoginRequest;
import org.springframework.stereotype.Service;

import java.util.ArrayList;
import java.util.List;
import java.util.Optional;

@Service
public class UserService {
    private List<User> users;

    public  UserService() {
        users = new ArrayList<>();
        users.add(new User(1, "Bod", "bob@gmail.com", "111", "data:image/jpeg;base64," +
                "tq5U8xcRRQYiQulyNrtf5w1VusEUAPP+K0+5xtxs65vdD/AEf6TT4GrmQRRTNl7N+n8SYwuJ24Uuhvqcxs5C5iDqBca2Gu8EUWLyDUJbSzvprI+KwiujKVFmve1hFFNRgZRjCdyMoNwbm/WG8UU6GPxRxs3mwXivFFLCoN4rxRQGK8BMUUAG3giigI/9k="));
        users.add(new User(2, "Tuyen", "tuyen@gmail.com", "111", "data:image/jpeg;base64,"));

    }

    // dang dto data transfer object
    public UserDto login(LoginRequest request){
        if (request.getUsername().isEmpty()|| request.getPassword().isEmpty()){
            throw new BadRequestException("username hoac password ko dc de trong");
        }
        Optional<User> userOptional= users.stream()
                .filter(user -> user.getUsername().equals(request.getUsername()) && user.getPassword().equals(request.getPassword()))
                .findFirst();
        if (userOptional.isEmpty()){
            throw new NotFoundException("user hoac password ko chinh xac");
        }
        return UserMapper.toUserDto(userOptional.get());
    }
}
